function addslashes_deep($value) &#123;
	if (empty($value)) &#123;
		return $value;
	&#125; else &#123;
		return is_array($value) ? array_map(&#39;addslashes_deep&#39;, $value) : addslashes($value);
	&#125;
&#125;

