    public static function get($key, $default &#x3D; NULL)
    &#123;
        $key &#x3D; self::$_prefix . $key;
        $value &#x3D; isset($_COOKIE[$key]) ? $_COOKIE[$key] : (isset($_POST[$key]) ? $_POST[$key] : $default);
