				new InvokerTransformer(&quot;invoke&quot;, new Class[] &#123; Object.class,Object[].class &#125;, 
                                       new Object[] &#123; null, new Object[0] &#125;),

				new InvokerTransformer(&quot;exec&quot;, new Class[]&#123;String.class&#125;,

